mapper.update:
UPDATE STORE
SET RATING = #{rating}
WHERE STORENUM = #{storeNum}

mapper.insert:
INSERT INTO ITEM_TABLE( ITEMNUM, STORENUM, ITEMNAME, ITEMCONTENT, ITEMPRICE
, ORIGINALFILE , SAVEDFILE
) VALUES ( ITEM_SEQ.NEXTVAL, #{storeNum}, #{itemName}, #{itemContent}, #{itemPrice}
, #{originalFile} , #{savedFile}
)

mapper.update:
UPDATE ORDER_TABLE
SET QUANTITY = QUANTITY + 1
WHERE ORDERNUM = #{orderNum}

UPDATE ORDER_TABLE
SET QUANTITY = QUANTITY - 1
WHERE ORDERNUM = #{orderNum}

mapper.select:
SELECT SUM(ITEMPRICE * quantity)
FROM ORDER_TABLE
WHERE COMPLETE = 'N' 
AND MEMBERNUM = #{memberNum}
AND STORENUM = #{storeNum}

mapper.update:
UPDATE ORDER_TABLE
SET COMPLETE = 'Y'
WHERE ORDERNUM = #{orderNum}

mapper.insert:
INSERT INTO WISHLIST(
	MEMBERNUM,
	MEMBERID,
	STORENUM
)
VALUES(
	#{memberNum},
	#{memberId},
	#{storeNum}
)

mapper.select:
SELECT * FROM
WISHLIST
WHERE STORENUM = #{storeNum} AND MEMBERNUM = #{memberNum}

mapper.update:
UPDATE STORE
SET WISHLIST = WISHLIST + 1
WHERE STORENUM = #{storeNum}

UPDATE STORE
SET WISHLIST = WISHLIST - 1
WHERE STORENUM = #{storeNum}
