mapper.insert:
INSERT INTO DELIVERY_MEMBER ( MEMBERNUM, MEMBERID, MEMBERPW, MEMBERNAME, NICKNAME, ADDRESS, PHONE
,PINNUMBER
)VALUES ( DELIVERY_MEMBER_SEQ.NEXTVAL, #{memberId}, #{memberPw}, #{memberName}, #{nickname}, #{address}, #{phone}
,#{pinNumber}
)

mapper.update:

	SELECT MAX(MEMBERNUM) FROM DELIVERY_MEMBER		


UPDATE DELIVERY_MEMBER
SET ROOMID = #{roomId}
WHERE MEMBERNUM = #{memberNum}

UPDATE DELIVERY_MEMBER
SET 

	MEMBERPW = #{memberPw}


	,MEMBERNAME = #{memberName}


	,NICKNAME = #{nickname}


	,ADDRESS = #{address}


	,PHONE = #{phone}

WHERE
	MEMBERID = #{memberId}

UPDATE DELIVERY_MEMBER
SET ADDRESS = #{address}, PHONE = #{phone}
WHERE MEMBERID = #{memberId}

UPDATE DELIVERY_MEMBER
SET MEMBERPOINT = MEMBERPOINT + #{memberPoint}
WHERE MEMBERID = #{memberId}

UPDATE DELIVERY_MEMBER
SET MEMBERPOINT = #{memberPoint}
WHERE MEMBERID = #{memberId}

UPDATE DELIVERY_MEMBER
SET ENABLED = 0
WHERE MEMBERID = #{memberId}

UPDATE DELIVERY_MEMBER
SET
	MEMBERPW = #{memberPw}
WHERE
	PINNUMBER = #{pinNumber}

UPDATE DELIVERY_MEMBER
SET MEMBEREXP = MEMBEREXP + 1000
WHERE MEMBERID = #{memberId}

UPDATE DELIVERY_MEMBER
SET MEMBEREXP = MEMBEREXP + 300
WHERE MEMBERID = #{memberId}

UPDATE DELIVERY_MEMBER
SET ORIGINALFILE = #{originalFile}, SAVEDFILE = #{savedFile}
WHERE MEMBERID = #{memberId}
